import "./App.css";

import { useForm } from "react-hook-form";

function App() {
  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    trigger,
  } = useForm();

  const onSubmit = (data) => {
    console.log(data);
    reset();
  };


  // console.log(watch());

  // console.log(errors.name)

  return (
            <><center> <img src="Eidverse logo.png" width="100" height= "100"  alt="" /> </center>

            <div className="toprightcornerbox">
            <input placeholder="Enter wallet address">
            </input>
            <button type="button" onclick="alert('Hello world!')">Click Me!</button>
            </div>

            <div className="mainbox">
      <div className="container">
        <div className="image">
          <img src="logoeid.png" alt="" />

        </div>
        <div className="balance">
          <h4 className="text">Balance <br /> You have 300 EID </h4>
        </div>



      </div>

      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="form-group">

          <input placeholder="Enter wallet address"
            type="text"
            className={`form-control ${errors.name && "invalid"}`}
            {...register("name", { required: "Name is Required" })}
            onKeyUp={() => {
              trigger("name");
            } } />
          {errors.name && (
            <small className="text-danger">{errors.name.message}</small>
          )}
        </div>
        <div className="form-group">
          <br />
          <input placeholder="Enter Amount"
            type="text"
            className={`form-control ${errors.age && "invalid"}`}
            {...register("age", {
              required: "Age is Required",
              min: {
                value: 13,
                message: "Minimum Required age is 13",
              },
              max: {
                value: 65,
                message: "Maximum allowed age is 65",
              },
              pattern: {
                value: /^[0-9]*$/,
                message: "Only numbers are allowed",
              }
            })}
            onKeyUp={() => {
              trigger("age");
            } } />
          {errors.age && (
            <small className="text-danger">{errors.age.message}</small>
          )}
        </div>
        <br />
        <input
          type="Award Token"
          className="btn btn-primary my-3"
          value="Award Token" />
      </form>


    </div></>
  );
}

export default App;
